// Parse Agent Response with Structured Output
const agentResponse = $input.all()[0];
let analysis = {};

try {
  // Agent with structured output should return parsed data directly
  if (agentResponse.analysis) {
    analysis = agentResponse.analysis;
  } else if (agentResponse.output && agentResponse.output.analysis) {
    analysis = agentResponse.output.analysis;
  } else {
    // Fallback parsing for unstructured response
    const responseText = agentResponse.text || agentResponse.output || JSON.stringify(agentResponse);

    if (responseText.includes('{')) {
      const jsonStart = responseText.indexOf('{');
      const jsonEnd = responseText.lastIndexOf('}') + 1;
      const jsonStr = responseText.substring(jsonStart, jsonEnd);
      analysis = JSON.parse(jsonStr);
    } else {
      throw new Error('No valid JSON found in response');
    }
  }

  // Ensure all required fields exist
  if (!analysis.session_analysis) {
    analysis.session_analysis = {
      project_detected: 'Unknown',
      work_type: 'Unknown',
      revenue_impact: 'Unknown',
      focus_quality: 'Unknown',
      confidence: 0.5
    };
  }

  if (!analysis.strategic_alerts) analysis.strategic_alerts = [];
  if (!analysis.recommendations) analysis.recommendations = [];
  if (!analysis.linear_actions) analysis.linear_actions = [];

} catch (error) {
  console.log('Parsing error:', error);
  analysis = {
    session_analysis: {
      project_detected: 'Parse Error',
      work_type: 'Unknown',
      revenue_impact: 'Unknown',
      focus_quality: 'Unknown', 
      confidence: 0
    },
    strategic_alerts: [{
      type: 'system_error',
      message: 'Agent response parsing failed: ' + error.message,
      severity: 'high'
    }],
    recommendations: ['Check agent configuration and response format'],
    linear_actions: []
  };
}

// Add metadata
analysis.timestamp = new Date().toISOString();
analysis.agent_raw_response = agentResponse;

return [analysis];

// Match Linear Actions with Existing Issues
const analysis = $('Parse Agent Response').all()[0];
const linearIssues = $input.all();

let actionsToExecute = [];

// Process each recommended action
analysis.linear_actions.forEach(action => {
  if (action.action === 'update_issue') {
    // Find matching issue by title or project keywords
    const matchingIssue = linearIssues.find(issue => {
      const titleMatch = issue.title.toLowerCase().includes(action.issue_title.toLowerCase());
      const projectMatch = analysis.session_analysis.project_detected !== 'Unknown' && 
                          issue.project?.name.toLowerCase().includes(analysis.session_analysis.project_detected.toLowerCase());
      return titleMatch || projectMatch;
    });

    if (matchingIssue && matchingIssue.state.name === 'Backlog') {
      actionsToExecute.push({
        type: 'update_status',
        issue_id: matchingIssue.id,
        issue_identifier: matchingIssue.identifier,
        issue_title: matchingIssue.title,
        current_status: matchingIssue.state.name,
        new_status: 'In Progress',
        reason: action.reason,
        confidence: analysis.session_analysis.confidence
      });
    }
  } else if (action.action === 'create_issue') {
    // Only create if confidence is high
    if (analysis.session_analysis.confidence > 0.7) {
      actionsToExecute.push({
        type: 'create_new',
        title: action.issue_title,
        description: `**Auto-created from activity detection**\n\n${action.reason}\n\n**Detected Context:**\n- Project: ${analysis.session_analysis.project_detected}\n- Work Type: ${analysis.session_analysis.work_type}\n- Revenue Impact: ${analysis.session_analysis.revenue_impact}\n- Confidence: ${Math.round(analysis.session_analysis.confidence * 100)}%`,
        project_hint: analysis.session_analysis.project_detected,
        work_type: analysis.session_analysis.work_type,
        revenue_impact: analysis.session_analysis.revenue_impact,
        team_id: '42918051-c76f-45cd-a15a-175978ec47c6'
      });
    }
  }
});

return actionsToExecute.map(action => ({ 
  ...action, 
  timestamp: new Date().toISOString(),
  analysis_context: analysis.session_analysis
}));

